generated.css:
/* css reset */
*,
*::after,
*::before {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}
a {
  color: inherit;
  text-decoration: none;
}
img {
  max-width: 100%;
  max-height: 100%;
}

/* style.css */
ul,
ol {
  list-style: none;
}
body {
  background-color: #eee;
}
.globalHeader {
  margin: 20px 20px;
}
.searchForm {
  display: flex;
  justify-content: space-between;
}
@media (min-width: 400px) {
  .searchForm {
    max-width: 400px;
    margin: 50px auto;
  }
}
.searchForm > input {
  width: 100%;
  height: 40px;
  margin-right: 10px;
  padding: 0 10px;
  border: none;
  border-radius: 4px;
}
.searchForm > button {
  white-space: nowrap;
  padding: 0 16px;
  border: none;
  border-radius: 4px;
  background: #4e6ef2;
  border: 1px solid #dddddd;
  color: #fff;
  font-size: 16px;
}
.globalMain {
  margin-left: auto;
  margin-right: auto;
}
@media (min-width: 400px) {
  .globalMain {
    min-width: 900px;
    max-width: 900px;
  }
}
.siteList {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  margin: 20px;
}
@media (min-width: 400px) {
  .siteList {
    justify-content: start;
    margin-right: -57px;
  }
}
.siteList > li {
  margin-bottom: 20px;
}
@media (min-width: 400px) {
  .siteList > li {
    margin-right: 25px;
  }
}
.siteList .site {
  position: relative;
  display: flex;
  width: 160px;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: #fff;
  padding: 15px 0;
  cursor: pointer;
}
.siteList .site > .logo {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 64px;
  height: 64px;
  font-size: 64px;
  text-transform: uppercase;
}
.siteList .site > .link {
  margin-top: 4px;
  font-size: 14px;
}
.siteList .site > .close {
  background-color: rgba(128, 134, 139, 0.2);
  display: none;
  position: absolute;
  top: 3px;
  right: 3px;
  cursor: default;
  height: 20px;
  width: 20px;
  border-radius: 50%;
}
.siteList .site:hover > .close {
  display: block;
}
.siteList .site > .close > .icon {
  position: absolute;
  top: 2px;
  right: 1.5px;
}
.siteList .addButton {
  background-color: #fff;
  width: 160px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  padding: 15px 0;
}
.siteList .addButton .icon-wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
}
.siteList .addButton .icon {
  width: 64px;
  height: 64px;
  padding: 0;
}
